function updateTime() {
    const clock = document.getElementById("clock");
    const now = new Date();

    // Menetapkan zona waktu Indonesia (WIB) dengan offset +7 jam
    const offset = 7 * 60 * 60 * 1000; // Offset 7 jam dalam milidetik
    const localTime = new Date(now.getTime() + offset);

    // Mendapatkan hari, tanggal, dan waktu dalam format HH:MM:SS
    const days = [
        "Minggu",
        "Senin",
        "Selasa",
        "Rabu",
        "Kamis",
        "Jumat",
        "Sabtu",
    ];
    const dayName = days[localTime.getUTCDay()];
    const date = String(localTime.getUTCDate()).padStart(2, "0");
    const month = String(localTime.getUTCMonth() + 1).padStart(2, "0"); // Bulan dimulai dari 0
    const year = localTime.getUTCFullYear();
    const hours = String(localTime.getUTCHours()).padStart(2, "0");
    const minutes = String(localTime.getUTCMinutes()).padStart(2, "0");
    const seconds = String(localTime.getUTCSeconds()).padStart(2, "0");

    const timeString = `${dayName}, ${date}/${month}/${year} ${hours}:${minutes}:${seconds}`;

    clock.textContent = timeString;
}

// Memperbarui waktu setiap detik
setInterval(updateTime, 1000);

// Menampilkan waktu segera setelah halaman dimuat
updateTime();
